import { Component, Inject } from '@angular/core';
import { MAT_DIALOG_DATA } from '@angular/material/dialog';
import { DomSanitizer, SafeResourceUrl } from '@angular/platform-browser';
@Component({
  selector: 'app-dialog-content',
  templateUrl: 'dialog-content.component.html',
  //styleUrls: ['dialog-content.component.css']
})
export class DialogContentComponent {
  iframeUrl: SafeResourceUrl;
  text:any
  constructor(@Inject(MAT_DIALOG_DATA) public data: { imageSrc: any, html: any, text:any }, private sanitizer: DomSanitizer) {

    const decodedHtml = atob(data.html);

    const dataUrl = 'data:text/html;base64,' + btoa(decodedHtml);

    this.iframeUrl = this.sanitizer.bypassSecurityTrustResourceUrl(dataUrl);
    this.text = data.text
  }
}
<mat-card class="custom-card">
    <!-- Botón de cierre y otros botones de acción -->
    <mat-card-actions class="card-actions">
        <button mat-icon-button mat-dialog-close>
            <mat-icon>close</mat-icon>
        </button>
    </mat-card-actions>

    <!-- Contenido principal, en este caso un iframe -->
    <mat-card-content >
        <iframe *ngIf="data.html" [src]="iframeUrl" width="100%" height="500" ></iframe>
    </mat-card-content>

    <!-- Footer con texto o leyenda -->
    <mat-card-footer class="card-footer">
        <span class="leyenda-texto">
            {{ text }}
        </span>
    </mat-card-footer>
</mat-card>
