<mat-card class="card-container">
  <mat-card-header>
    <mat-card-title>
      <h1>Aprendizaje profundo en las hojas de la Yuca</h1>
      <h2>Seleccione una hoja de datos en formato CSV</h2>
    </mat-card-title>
  </mat-card-header>
  <mat-card-content>
    <div class="file-upload-container">
      <input class="form-control file-input" type="file" name="archivo" id="archivo" (change)="getData($event)">
    </div>
    <div class="buttons-container">
      <div class="button-container">
        <button mat-raised-button color="primary" (click)="yuca()">Buscar ejemplos de hojas sanas</button>
      </div>
      <div class="button-container">
        <button mat-raised-button color="primary" (click)="yuca_CBB()">Enfermedad Tizón bacteriano de la yuca (CBB)</button>
      </div>
      <div class="button-container">
        <button mat-raised-button color="primary" (click)="yuca_CGM()">Enfermedad Moteado verde de la yuca (CGM)</button>
      </div>
      <div class="button-container">
        <button mat-raised-button color="primary" (click)="yuca_CDM()">Enfermedad del mosaico de la yuca (CMD)</button>
      </div>
      <div class="button-container">
        <button mat-raised-button color="primary" (click)="yuca_CBSD()">Enfermedad de la raya marrón de la yuca (CBSD)</button>
      </div>
    </div>
  </mat-card-content>
</mat-card>

