<mat-card-content *ngIf="!loading && mostrarEjemplos" class="full-width">
  <h4 class="mt-5">Estos son varios ejemplos</h4>
  <br>
  <div class="image-container">
    <div *ngFor="let grafico of graficos; let i = index" class="image-item">
      <mat-card>
        <mat-card-header>
          <mat-panel-title>
            Ejemplo de planta {{ i + 1 }}
          </mat-panel-title>
          <mat-panel-description class="float-end">
            <a *ngIf="grafico.tipo == 'foto'" [href]="'data:image/png;base64,' + grafico.base64" download="grafico{{ i }}.png" class="btn btn-light float-end">
              <mat-icon aria-hidden="false" aria-label="" fontIcon="file_download"></mat-icon>
            </a>
          </mat-panel-description>
        </mat-card-header>
        <mat-card-content>

          <img [src]="'data:image/png;base64,' + grafico" 
          draggable="false"class="card-img-bottom imagen-hover" 
          [alt]="'Gráfico ' + (i + 1)" 
          (click)="openImageDialog(grafico)">

        </mat-card-content>
      </mat-card>
    </div>
  </div>
</mat-card-content>

<mat-card *ngIf="loading" class="mx-auto">
  <div class="spinner-container">
    <mat-spinner diameter="48" color="primary"></mat-spinner>
  </div>
</mat-card>
